module.exports = function(grunt)
{
  /**
   * cleans instrumented files generated by coverage i.e _bak directories
   */
  grunt.registerTask('clean-instrumented', 'cleans instrumented files created by coverage', function()
  {
    var asyncblock = require('asyncblock');

    var done = this.async()

    asyncblock(function(syncflow)
    {
      grunt.helper('coverage:clean-instrumented', grunt.config('coverage').dirs, syncflow)

      done()
    });
  })
}